<?php get_header(); ?><!-- Content -->			<div id="content">				<div class="main">					<div class="blog_wrapp">	<!-- Blog Content -->						<div class="blog_content">							<?php if (have_posts()) : ?>								<?php while (have_posts()) : the_post(); // the loop ?>									<div class="post post_index" id="post-<?php the_ID(); ?>">										<h2><a href="<?php the_permalink() ?>" rel="bookmark" title="Permanent Link to <?php the_title_attribute(); ?>"><?php the_title(); ?></a></h2>										<p class="posted">Posted by  <?php the_author_posts_link(); ?>   on <?php the_time('F jS, Y') ?>. &nbsp;&nbsp; Category: 											<?php $category = get_the_category(); 											$category_id_l = get_cat_ID( $category[0]->cat_name );											$category_link_n = get_category_link( $category_id_l );?>											<a href="<?php echo $category_link_n; ?>"><?php echo $category[0]->cat_name; ?></a>										</p>										<?php the_content('Read more'); ?>										<p class="tags_post">TAGS: <?php the_tags(' ', ' ', '<br />'); ?></p>										<?php include (TEMPLATEPATH . "/share_block.php"); ?>									</div>								<?php endwhile; ?>								<ul class="post_links">									<li class="next_blog"><?php previous_posts_link('Previous Entries') ?></li>									<li class="prev_blog"><?php next_posts_link('Next Entries') ?></li>								</ul>							<?php else : ?>								<div class="post">									<h2 class="center">Not Found</h2>									<p class="center">Sorry, but you are looking for something that isn't here.</p>								</div>							<?php endif; ?>						</div>						<?php get_sidebar(); ?>					</div>				</div>			</div>		</div><?php get_footer(); ?>