template.template_content:
{parse js_module="status"}
{parse striping="recent_status" classes="row1,row2"}
<div class='status_profile_page row2 altrow'>
	<if test="canCreate:|:$this->memberData['member_id'] AND ( $this->memberData['member_id'] == $member['member_id'] ) AND $this->registry->getClass('memberStatus')->canCreate( $member )">
		<div class='status_update'>
			<form id='statusForm' action='{$this->settings['base_url']}app=members&amp;module=profile&amp;section=status&amp;do=new&amp;k={$this->member->form_hash}&amp;id={$this->memberData['member_id']}' method='post'>
			<input type='text' name='content' id='statusUpdate' /> <input type='submit' class='input_submit alt' id='statusSubmit' value='{$this->lang->words['_rarr']}' />
			<if test="update:|:(IPSLib::twitter_enabled() OR IPSLib::fbc_enabled() ) AND ( $this->memberData['fb_uid'] OR $this->memberData['twitter_id'] )">
				<p class='desc' style='padding-top:5px;'>{$this->lang->words['st_update']}
					<if test="updateTwitter:|:IPSLib::twitter_enabled() AND ( $this->memberData['twitter_id'] )"><input type='checkbox' id='su_Twitter' value='1' name='su_Twitter' /> <img src="{$this->settings['public_dir']}style_status/twitter.png" style='vertical-align:top' alt='' /></if>
					<if test="updateFacebook:|:IPSLib::fbc_enabled() AND ( $this->memberData['fb_uid'] )">&nbsp;<input type='checkbox' id='su_Facebook' value='1' name='su_Facebook' /> <img src="{$this->settings['public_dir']}style_status/facebook.png" style='vertical-align:top' alt='' /></if>
				</p>
			</if>
			</form>
		</div>
	</if>
	<div id="status_wrapper">
		<if test="hasUpdates:|:count( $updates )">
			{parse template="statusUpdates" group="profile" params="$updates"}
			<br />
			<div class='right'>
				<a href='{parse url="app=members&amp;module=profile&amp;section=status&amp;member_id={$member['member_id']}" seotitle="true" template="members_status_all" base="public"}'>{$this->lang->words['status__viewall']}</a>
			</div>
		<else />
			<p class='no-status'>{$this->lang->words['status_updates_none']}</p>
		</if>
	</div>
</div>
<div class='status_profile_actions'>
	<if test="count( $actions )">
		<foreach loop="actions:$actions as $id => $row">
			<div class='{parse striping="recent_status"} clear'>
				<div class='status_action_left'><img src="{$row['pp_mini_photo']}" width="{$row['pp_mini_width']}" height="{$row['pp_mini_height']}" alt="" /></div>
				<div class='status_action_right'>
					{parse expression="sprintf(
						$this->lang->words['status__repliedstring'],
						"{parse url="showuser={$row['member_id']}" seotitle="{$row['members_seo_name']}" template="showuser" base="public"}",
						"{$row['members_display_name']}",
						"{parse template="user_popup" group="global" params="$row['member_id'],$row['members_seo_name']"}",
						"{parse url="showuser={$row['owner']['member_id']}" seotitle="{$row['owner']['members_seo_name']}" template="showuser" base="public"}",
						"{$row['owner']['members_display_name']}",
						"{parse template="user_popup" group="global" params="$row['owner']['member_id'],$row['owner']['members_seo_name']"}",
						"{parse url="app=members&amp;module=profile&amp;section=status&amp;type=all&amp;status_id={$row['action_status_id']}" seotitle="true" template="members_status_all" base="public"}"
					)"}
				<br /><span class='desc'>{$row['action_date_formatted_short']}</span>
				</div>
			</div>
		</foreach>
	<else />
		<p class='no-status'>{$this->lang->words['status_actions_none']}</p>
	</if>
</div>
<script type='text/javascript'>
document.observe("dom:loaded", function(){
	function _resizeBlocks()
	{
		var status  = $$('.status_profile_page')[0];
		var actions = $$('.status_profile_actions')[0];
		var s_h     = status.getHeight();
		var a_h		= actions.getHeight();

		if ( a_h && s_h && a_h > s_h )
		{
			actions.setStyle( { height: s_h + 'px', overflow: 'auto' } );
		}

		setTimeout( _resizeBlocks, 1000 );
	}

	_resizeBlocks();
});
</script>
